package javaPackage;

public class Add {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c = a+b;
		int d = a*b;
		int e= b/a;
		int f =b-a;
		
		int g= b % a;
		System.out.println("Addition "+c);
		System.out.println("Product "+d);
		System.out.println("Division "+e);
		System.out.println("Substract "+f);
		System.out.println("Modulus "+g);

	}

}
